import React from 'react';
import { useHistory } from 'react-router-dom';


// Data for Q1 and Q2 options
const optionsQ1 = [
    'Bus',
    'Metro',
    'Own Two-wheeler',
    'Own Car',
    'Walk / Bicycle',
    'Auto',
    'App based ride hailing cab services including Ola / Uber',
];

const optionsQ2 = [
    '< 5 km',
    '5 - 10 km',
    '10 - 15 km',
    '15 - 20 km',
    '20 - 25 km',
    '> 25 km',
];

const Home = ({ handleOptionChangeQ2 }) => {
    const history = useHistory();

    const handleSubmit = (event) => {
      event.preventDefault();
      const formData = new FormData(event.target);
      const selectedOptionQ2 = formData.get('q2');
      handleOptionChangeQ2({ target: { value: selectedOptionQ2 } });
      history.push('/mode-choice');
    };


    return (
        <div>
            <h1>Respondent Travel Profile</h1>
            <h2>Q1: What is your most frequently used means of travel from your home to work location?</h2>
            <form>
                {optionsQ1.map((option, index) => (
                    <label key={index}>
                        <input
                            type="radio"
                            value={option}
                            checked={selectedOptionQ1 === option}

                        />
                        {option}
                    </label>
                ))}
            </form>
            <h2>Q2: What is the total distance between your home and workplace?</h2>
            {/* <form>
                {optionsQ2.map((option, index) => (
                    <label key={index}>
                        <input
                            type="radio"
                            value={option}
                            checked={selectedOptionQ2 === option}
                            onChange={handleOptionChangeQ2}
                        />
                        {option}
                    </label>
                ))}
            </form> */}
            <Link to="/mode-choice">
                <button className=''> Next</button>
            </Link>

        </div>
    )
}

export default Home
