// Home.js

import React, { useState } from 'react';

const Home = ({ handleQuestion2Change }) => {
    const [question2, setQuestion2] = useState('');

    const handleQuestion2Selection = (e) => {
      const value = e.target.value;
      setQuestion2(value);
      handleQuestion2Change(value);
    }


  return (
    <div>
      <h2>Question 2: What is the total distance between your home and workplace?</h2>
      <form>
        <input type="radio" name="distance" value="<5km" onChange={handleQuestion2Selection} />
        <label htmlFor="<5km">Less than 5 km</label><br />
        <input type="radio" name="distance" value=">5km" onChange={handleQuestion2Selection} />
        <label htmlFor=">5km">Greater than 5 km</label><br />
      </form>
      {question2 && (
        <div>
          <h3>You selected: {question2}</h3>
          <button onClick={() => window.location.href = '/mode-choice'}>Proceed to Mode Choice</button>
        </div>
      )}
    </div>
  );
}

export default Home;
