import React, { useState } from "react";
import { Link } from "react-router-dom";

const HomePage = () => {
  const [distance, setDistance] = useState("");

  const handleDistanceChange = (e) => {
    setDistance(e.target.value);
  };

  return (
    <div>
      <h1>Home Page</h1>
      <h2>Question:</h2>
      <p>What is the total distance between your home and workplace?</p>
      <form>
        <label>
          <input
            type="radio"
            value="<5km"
            checked={distance === "<5km"}
            onChange={handleDistanceChange}
          />
          &lt; 5 km
        </label>
        <label>
          <input
            type="radio"
            value=">5km"
            checked={distance === ">5km"}
            onChange={handleDistanceChange}
          />
          &gt; 5 km
        </label>
      </form>
      {distance && (
        <Link
          to={{
            pathname: "/modechoice",
            state: { distance: distance } // Pass the distance state as an object
          }}
        >
          Continue
        </Link>
      )}
    </div>
  );
};

export default HomePage;
