import React, { useState } from 'react';
import { BrowserRouter, Routes, Route } from "react-router-dom";
import Home from './pages/Home'
import ModeChoice from './pages/ModeChoice'


const App = () => {
  const [selectedOptionQ2, setSelectedOptionQ2] = useState('');

  const handleOptionChangeQ2 = (event) => {
    setSelectedOptionQ2(event.target.value);
  };



  return (
    <BrowserRouter>
      <Routes>
        <Route exact path="/" render={(props) => <Home {...props} handleOptionChangeQ2={handleOptionChangeQ2} />} />
        <Route path="/mode-choice" render={(props) => <ModeChoice {...props} selectedOptionQ2={selectedOptionQ2} />} />


      </Routes>
    </BrowserRouter>
  )
}

export default App
